import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and extract prices
    await page.waitForSelector('.listing-card, [data-testid="listing-card"]', { timeout: 10000 });

    // Extract all listing prices using multiple selector strategies
    const prices = await page.$$eval(
      '.listing-price, .price-per-night, [data-testid="listing-price"], [class*="price"]',
      elements => elements.map(el => {
        const priceText = el.textContent.replace(/[^\d.]/g, '');
        return parseFloat(priceText) || 0;
      })
    );

    // Store original prices in localStorage
    await page.evaluate((prices) => {
      localStorage.setItem('originalPrices', JSON.stringify(prices));

      // Create pricing history object if it doesn't exist
      if (!window.airbnbData) window.airbnbData = {};
      if (!window.airbnbData.pricingHistory) {
        window.airbnbData.pricingHistory = {
          original: prices,
          modified: prices.map(price => price * 0.9),
          timestamp: new Date().toISOString()
        };
      }
    }, prices);

    // Apply 10% discount to displayed prices
    await page.$$eval(
      '.listing-price, .price-per-night, [data-testid="listing-price"], [class*="price"]',
      (elements) => {
        elements.forEach(el => {
          const priceText = el.textContent.replace(/[^\d.]/g, '');
          const originalPrice = parseFloat(priceText) || 0;
          if (originalPrice > 0) {
            const discountedPrice = (originalPrice * 0.9).toFixed(2);
            el.textContent = `$${discountedPrice}`;

            // Add visual indication of discount
            el.style.color = '#ff385c';
            el.style.fontWeight = 'bold';
          }
        });
      }
    );

    // Verify changes were applied
    const discountedPrices = await page.$$eval(
      '.listing-price, .price-per-night, [data-testid="listing-price"], [class*="price"]',
      elements => elements.map(el => parseFloat(el.textContent.replace(/[^\d.]/g, '')) || 0)
    );

    // Take screenshot after modification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discounted_prices.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discounted_prices.png'));

    return {
      success: true,
      action: "Extracted all listing prices, stored in localStorage, and displayed 10% discounted prices",
      extractedData: {
        originalPrices: prices,
        discountedPrices: discountedPrices,
        localStorageData: await page.evaluate(() => localStorage.getItem('originalPrices'))
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
