import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the 'Electronics' link in the navigation menu
    await page.waitForSelector('.nav-links a[onclick*="electronics"]');
    await page.click('.nav-links a[onclick*="electronics"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to Electronics category
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_navigated_to_electronics.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_navigated_to_electronics.png'));

    // Check if the navigation was successful by verifying the URL or content
    const currentUrl = page.url();
    const electronicsPageLoaded = await page.evaluate(() => {
      return document.querySelector('h1') && document.querySelector('h1').innerText.includes('Electronics');
    });

    return {
      success: electronicsPageLoaded,
      action: electronicsPageLoaded ? "Successfully navigated to Electronics category" : "Failed to navigate to Electronics category",
      extractedData: {
        currentUrl,
        title: await page.title()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
