import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the 'Electronics' link in the navigation menu
    await page.waitForSelector('.nav-links a[onclick*="electronics"]');
    await page.click('.nav-links a[onclick*="electronics"]');
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigating to Electronics category
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_navigated_to_electronics.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_navigated_to_electronics.png'));

    // Verify navigation by checking the URL or a specific element on the electronics page
    const currentUrl = page.url();
    const electronicsPageCheck = await page.$('.category-card[onclick*="electronics"]');

    return {
      success: true,
      action: "Navigated to Electronics category",
      extractedData: {
        currentUrl,
        isElectronicsPage: electronicsPageCheck !== null
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

