import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the Electronics link to be visible and then click it
    await page.waitForSelector('a[href="#"]', { timeout: 5000 });
    const electronicsLink = await page.$('a[href="#"][onclick*="electronics"]');
    if (electronicsLink) {
      await electronicsLink.click();
    } else {
      throw new Error('Electronics link not found');
    }

    // Wait for the navigation to complete
    await page.waitForLoadState('networkidle');

    // Take a screenshot after navigating to the Electronics category
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_navigated_to_electronics.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_navigated_to_electronics.png'));

    // Verify navigation by checking if the page contains expected content
    const electronicsPageContent = await page.content();
    if (!electronicsPageContent.includes('Electronics')) {
      throw new Error('Failed to navigate to Electronics category');
    }

    return {
      success: true,
      action: "Navigated to the Electronics category page",
      extractedData: { category: 'Electronics' },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
