import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the navigation menu to be visible
    await page.waitForSelector('.nav-links');

    // Click on the 'Electronics' link in the navigation menu
    await page.click('a[onclick*="electronics"]');

    // Wait for the electronics category page to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the electronics category page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_category.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_category.png'));

    // Verify that the navigation to the electronics category was successful
    const pageTitle = await page.title();
    const isElectronicsPage = pageTitle.includes('Electronics');

    return {
      success: true,
      action: "Navigated to the Electronics category page",
      extractedData: { isElectronicsPage },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

