import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-links');

    // Click on Electronics link using multiple selector strategies
    const electronicsLink = await page.$('.nav-links a[onclick*="electronics"]');
    if (electronicsLink) {
      await electronicsLink.click();
    } else {
      throw new Error('Electronics link not found in navigation menu');
    }

    // Wait for page to update after navigation
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for content to render

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_page.png'));

    // Extract verification data
    const pageTitle = await page.title();
    const currentUrl = page.url();
    const electronicsHeading = await page.$eval('h1', el => el.textContent).catch(() => 'Not found');

    return {
      success: true,
      action: "Successfully navigated to Electronics category",
      extractedData: {
        pageTitle,
        currentUrl,
        heading: electronicsHeading
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

