import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to be visible
    await page.waitForSelector('.nav-bar', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_nav_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_nav_visible.png'));

    // Click Electronics link using multiple selector strategies
    const electronicsLink = await page.waitForSelector('.nav-links a[onclick*="electronics"], text=Electronics', { state: 'visible' });
    await electronicsLink.click();

    // Wait for content to load after navigation
    await page.waitForTimeout(1000);
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_electronics_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_electronics_page.png'));

    // Verify navigation by checking page content
    const pageTitle = await page.title();
    const electronicsHeading = await page.$eval('h1, h2, h3', el => el.textContent).catch(() => '');

    return {
      success: true,
      action: "Successfully navigated to Electronics category by clicking the navigation link",
      extractedData: {
        pageTitle,
        heading: electronicsHeading.includes('Electronics') ? electronicsHeading : 'No electronics heading found'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
