import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu and click Electronics link
    await page.waitForSelector('.nav-links');
    await page.click('.nav-links a:has-text("Electronics")');

    // Wait for category page to load
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for content to render

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_category.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_category.png'));

    // Verify navigation by checking page content
    const categoryTitle = await page.$eval('h1, .category-title, .page-title', el => el.textContent.trim()).catch(() => null);
    const productCount = await page.$$eval('.product-card, .product-item', items => items.length).catch(() => 0);

    return {
      success: true,
      action: "Navigated to Electronics category",
      extractedData: {
        categoryTitle: categoryTitle || 'Electronics Category',
        productCount: productCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
      screenshots.push(path.join(screenshotsDir, 'step_error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to be visible
    await page.waitForSelector('.nav-links', { state: 'visible' });

    // Take screenshot before interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_nav_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_nav_visible.png'));

    // Click on Electronics link using multiple selector strategies
    const electronicsLink = await page.$('.nav-links a[onclick*="electronics"]');

    if (!electronicsLink) {
      // Fallback to text-based selector
      await page.click('text=Electronics', { timeout: 5000 });
    } else {
      await electronicsLink.click();
    }

    // Wait for category change to complete
    await page.waitForTimeout(1000);

    // Verify navigation by checking if electronics content is loaded
    const electronicsContent = await page.$eval('body', (body) => {
      return body.textContent.includes('Electronics') || 
             body.textContent.includes('electronics') ||
             Array.from(body.querySelectorAll('*'))
               .some(el => el.onclick && el.onclick.toString().includes('electronics'));
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_electronics_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_electronics_loaded.png'));

    return {
      success: true,
      action: "Successfully navigated to Electronics category",
      extractedData: {
        electronicsCategoryLoaded: electronicsContent,
        currentView: await page.evaluate(() => window.currentView || 'unknown')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

