  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-links', { timeout: 5000 });

    // Click on Electronics link using multiple selector strategies
    const electronicsLink = await page.$('.nav-links a[onclick*="electronics"]') || 
                           await page.$('a:has-text("Electronics")');

    if (!electronicsLink) {
      throw new Error('Electronics link not found');
    }

    await electronicsLink.click();
    await page.waitForLoadState('networkidle');

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_page.png'));

    // Verify navigation by checking page content
    const pageTitle = await page.title();
    const pageContent = await page.textContent('body');
    const hasElectronicsContent = pageContent.toLowerCase().includes('electronics');

    return {
      success: true,
      action: "Successfully navigated to Electronics category",
      extractedData: {
        pageTitle,
        hasElectronicsContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-links', { timeout: 5000 });

    // Click Electronics link using multiple selector strategies
    const electronicsSelectors = [
      '.nav-links a[onclick*="electronics"]',
      'text=Electronics',
      'a:has-text("Electronics")'
    ];

    let clicked = false;
    for (const selector of electronicsSelectors) {
      try {
        await page.click(selector, { timeout: 2000 });
        clicked = true;
        break;
      } catch (error) {
        console.log(`Selector ${selector} failed, trying next...`);
      }
    }

    if (!clicked) {
      throw new Error('Could not find Electronics link with any selector');
    }

    // Wait for page to update after navigation
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for UI updates

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_electronics_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_electronics_page.png'));

    // Verify navigation by checking page content
    const pageTitle = await page.title();
    const electronicsContent = await page.textContent('body');

    return {
      success: true,
      action: "Successfully navigated to Electronics category",
      extractedData: {
        pageTitle: pageTitle,
        containsElectronics: electronicsContent.toLowerCase().includes('electronics'),
        url: page.url()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-links', { timeout: 5000 });

    // Multiple selector strategies for Electronics link
    const electronicsSelectors = [
      '.nav-links a[onclick*="electronics"]',
      'a:has-text("Electronics")',
      'text=Electronics'
    ];

    let electronicsLink = null;
    for (const selector of electronicsSelectors) {
      try {
        electronicsLink = await page.$(selector);
        if (electronicsLink) break;
      } catch (error) {
        continue;
      }
    }

    if (!electronicsLink) {
      throw new Error('Electronics link not found in navigation menu');
    }

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the Electronics link
    await electronicsLink.click();
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(2000); // Additional wait for content to load

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_navigation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_navigation.png'));

    // Verify navigation by checking page content
    const pageTitle = await page.title();
    const electronicsHeading = await page.$('h1:has-text("Electronics")') || 
                              await page.$('.category-title:has-text("Electronics")');

    return {
      success: true,
      action: "Successfully navigated to Electronics category",
      extractedData: {
        pageTitle: pageTitle,
        hasElectronicsHeading: !!electronicsHeading
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });
    screenshots.push(errorScreenshot);

    return {
      success: false,
      action: "Failed to navigate to Electronics category",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
