import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput');

    // Type 'iPhone' in the search input field
    await page.fill('#searchInput', 'iPhone');

    // Take a screenshot after entering the search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click the search button
    await page.click('.search-btn');

    // Wait for the search results to load
    await page.waitForSelector('.search-results'); // Assuming there's a class for search results

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract data to verify task completion
    const results = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.search-results .product-title')).map(product => product.innerText);
    });

    return {
      success: true,
      action: "Searched for 'iPhone' products",
      extractedData: { results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

