import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be visible
    await page.waitForSelector('#searchInput');

    // Type 'iPhone' in the search input field
    await page.fill('#searchInput', 'iPhone');

    // Wait for the search button to be visible and click it
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Take a screenshot after performing the search
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Extract meaningful data to verify task completion
    const searchResults = await page.$$eval('.s-result-item', items => items.map(item => item.innerText));

    return {
      success: true,
      action: "Searched for 'iPhone' products successfully",
      extractedData: { searchResults },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

