import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field and type 'iPhone'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iPhone');

    // Take screenshot after entering search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Wait for the search button and click it
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Take screenshot after clicking search button
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract meaningful data to verify task completion
    const results = await page.evaluate(() => {
      const items = Array.from(document.querySelectorAll('.s-main-slot .s-result-item'));
      return items.map(item => ({
        title: item.querySelector('h2') ? item.querySelector('h2').innerText : null,
        price: item.querySelector('.a-price') ? item.querySelector('.a-price').innerText : null,
        link: item.querySelector('h2 a') ? item.querySelector('h2 a').href : null,
      }));
    });

    return {
      success: true,
      action: "Searched for 'iPhone' and retrieved product results",
      extractedData: results,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

