import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be available
    await page.waitForSelector('#searchInput');

    // Input 'iPhone' into the search field
    await page.fill('#searchInput', 'iPhone');

    // Take a screenshot after entering the search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click the search button to perform the search
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for the search results to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the results page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extracting meaningful data to verify task completion
    const searchResults = await page.$$eval('.search-results .product-title', titles => titles.map(title => title.textContent));

    return {
      success: true,
      action: "Searched for 'iPhone' and retrieved search results",
      extractedData: { searchResults },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
