import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field to be available and type 'iPhone'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iPhone');

    // Take a screenshot after entering the search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Wait for the search button to be available and click it
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for the results to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the results page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_results_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_results_page.png'));

    // Extract meaningful data to verify task completion
    const results = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.s-main-slot .s-result-item')).map(item => ({
        title: item.querySelector('h2') ? item.querySelector('h2').innerText : null,
        price: item.querySelector('.a-price') ? item.querySelector('.a-price').innerText : null,
        link: item.querySelector('h2 a') ? item.querySelector('h2 a').href : null
      }));
    });

    return {
      success: true,
      action: "Searched for 'iPhone' and captured results",
      extractedData: results,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

