    return {
      success: true,
      action: "Searched for 'iPhone' and extracted product results",
      extractedData: { results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
