import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input field and enter 'iPhone'
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'iPhone');

    // Take a screenshot after entering the search term
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_term_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_term_entered.png'));

    // Wait for the search button and click it
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for the results to load
    await page.waitForLoadState('networkidle');

    // Take a screenshot of the search results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Extract meaningful data to verify task completion
    const results = await page.$$eval('.s-result-item', items => items.map(item => item.innerText));

    return {
      success: true,
      action: "Searched for 'iPhone' and captured search results",
      extractedData: { results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

