import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible
    await page.waitForSelector('#searchInput', { timeout: 5000 });

    // Type 'iPhone' into the search input
    await page.fill('#searchInput', 'iPhone');

    // Wait for the search button to be visible and click it
    await page.waitForSelector('.search-btn', { timeout: 5000 });
    await page.click('.search-btn');

    // Take screenshot after performing the search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_performed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_performed.png'));

    // Wait for the search results to load (modify this based on actual results loading)
    await page.waitForSelector('.search-results-container', { timeout: 5000 });

    // Extract the search results titles for verification
    const results = await page.evaluate(() => {
      return Array.from(document.querySelectorAll('.product-title')).map(element => element.innerText);
    });

    return {
      success: true,
      action: "Searched for 'iPhone' products successfully",
      extractedData: { results },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
