      success: true,
      action: "Successfully searched for 'iPhone' products",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input to be visible using multiple selector strategies
    await page.waitForSelector('#searchInput', { state: 'visible' });
    await page.waitForSelector('input[placeholder="Search Amazon"]');
    await page.waitForSelector('.search-input');

    // Type 'iPhone' into the search input using multiple selector strategies
    await page.fill('#searchInput', 'iPhone');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed_search.png'));

    // Wait for search button to be visible using multiple selector strategies
    await page.waitForSelector('.search-btn', { state: 'visible' });
    await page.waitForSelector('button[onclick="performSearch()"]');

    // Click search button using multiple selector strategies
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');

    // Wait for search results to load and take screenshot
    await page.waitForTimeout(1000); // Brief wait for UI update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Verify search was successful by checking URL and page content
    const currentUrl = page.url();
    const pageTitle = await page.title();
    const searchResults = await page.textContent('body');

    return {
      success: true,
      action: "Successfully searched for 'iPhone' by typing in search input and clicking search button",
      extractedData: {
        url: currentUrl,
        title: pageTitle,
        hasSearchResults: searchResults.includes('iPhone') || searchResults.includes('Search Results')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to search for 'iPhone'",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input using multiple selector strategies
    const searchInput = await page.waitForSelector('#searchInput, input[placeholder="Search Amazon"]', { state: 'visible' });

    // Clear input and type 'iPhone'
    await searchInput.fill('');
    await searchInput.type('iPhone', { delay: 50 });

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed.png'));

    // Click search button using multiple selector strategies
    const searchButton = await page.waitForSelector('.search-btn, button[onclick*="performSearch"]', { state: 'visible' });
    await searchButton.click();

    // Wait for search results to load
    await page.waitForLoadState('networkidle');
    await page.waitForTimeout(1000); // Additional wait for UI updates

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_results.png'));

    // Extract search results information
    const extractedData = {
      searchTerm: 'iPhone',
      resultsCount: await page.evaluate(() => {
        const results = document.querySelectorAll('.product-item, .product-card');
        return results.length;
      }),
      firstProduct: await page.evaluate(() => {
        const firstProduct = document.querySelector('.product-item, .product-card');
        return firstProduct ? firstProduct.textContent.trim().substring(0, 100) : 'No products found';
      })
    };

    return {
      success: true,
      action: "Successfully searched for 'iPhone' products",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete search task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
