import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to be visible
    await page.waitForSelector('.product-card:first-child');

    // Click the 'Add to Cart' button on the first product card
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for the cart count to update
    await page.waitForSelector('#cartCount');

    // Take a screenshot after adding the product to the cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_add_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_add_to_cart.png'));

    // Extract the updated cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added the first product to the cart",
      extractedData: { cartCount },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

