import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Find the first product card
    const firstProductCard = await page.$('.product-card:first-child');
    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Take screenshot of first product
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_first_product.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_first_product.png'));

    // Find and click the Add to Cart button using multiple selector strategies
    const addToCartButton = await firstProductCard.$('.add-to-cart') || 
                           await firstProductCard.$('button:has-text("Add to Cart")') ||
                           await firstProductCard.$('button:has-text("Add to cart")');

    if (!addToCartButton) {
      throw new Error('Add to Cart button not found in first product card');
    }

    // Get initial cart count
    const initialCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);

    // Click the Add to Cart button
    await addToCartButton.click();

    // Wait for cart count to update (indicating successful addition)
    await page.waitForFunction(
      (initialCount) => {
        const cartCount = parseInt(document.querySelector('#cartCount')?.textContent || 0);
        return cartCount > initialCount;
      },
      { timeout: 5000 },
      initialCartCount
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_added_to_cart.png'));

    // Extract product information
    const productTitle = await firstProductCard.$eval('.product-title', el => el.textContent.trim());
    const productPrice = await firstProductCard.$eval('.product-price', el => el.textContent.trim());
    const finalCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent));

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle,
        productPrice,
        initialCartCount,
        finalCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'step_error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}
