import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Find the first product card using XPath
    const firstProductCard = await page.$('(//*[contains(@class, "product-card")])[1]');

    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Get product details before adding to cart
    const productTitle = await firstProductCard.$eval('.product-title', el => el.textContent.trim());
    const productPrice = await firstProductCard.$eval('.product-price', el => el.textContent.trim());

    // Get initial cart count
    const initialCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);

    // Take screenshot before adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_add.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_add.png'));

    // Click the Add to Cart button using multiple selector strategies
    const addButton = await firstProductCard.$('button.add-to-cart, .add-to-cart-button, [class*="add-to-cart"]');

    if (!addButton) {
      // Fallback: try to find button by text content
      await firstProductCard.click({ button: 'left' });
      await page.click('text/Add to Cart', { timeout: 2000 });
    } else {
      await addButton.click();
    }

    // Wait for cart count to update
    await page.waitForFunction(
      (initialCount) => {
        const cartCount = document.querySelector('#cartCount');
        return cartCount && parseInt(cartCount.textContent) > initialCount;
      },
      { timeout: 5000 },
      initialCartCount
    );

    // Get updated cart count
    const updatedCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent));

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_add.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_add.png'));

    return {
      success: true,
      action: "Successfully added first product to cart",
      extractedData: {
        productTitle,
        productPrice,
        initialCartCount,
        updatedCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}
