import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first product card's Add to Cart button
    await page.waitForSelector('.product-card:first-child .add-to-cart', { timeout: 5000 });

    // Extract product info before adding to cart
    const productInfo = await page.evaluate(() => {
      const productCard = document.querySelector('.product-card:first-child');
      return {
        title: productCard.querySelector('.product-title')?.innerText || 'Unknown Product',
        price: productCard.querySelector('.product-price')?.innerText || 'Unknown Price'
      };
    });

    // Click the Add to Cart button
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.innerText !== '0';
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count
    const cartCount = await page.$eval('#cartCount', el => el.innerText);

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle: productInfo.title,
        productPrice: productInfo.price,
        cartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

