      return {
        title: firstCard.querySelector('.product-title')?.textContent?.trim(),
        price: firstCard.querySelector('.product-price')?.textContent?.trim(),
        rating: firstCard.querySelector('.product-rating')?.textContent?.trim()
      };
    });

    // Click the Add to Cart button using multiple selector strategies
    const addToCartSelectors = [
      '.product-card:first-child .add-to-cart',
      '.product-card:first-child button:has-text("Add to Cart")',
      'xpath=//div[contains(@class, "product-card")][1]//button[contains(text(), "Add to Cart")]'
    ];

    let addedToCart = false;
    for (const selector of addToCartSelectors) {
      try {
        await page.waitForSelector(selector, { timeout: 3000 });
        await page.click(selector);
        addedToCart = true;
        break;
      } catch (error) {
        console.log(`Selector ${selector} failed, trying next...`);
      }
    }

    if (!addedToCart) {
      throw new Error('Could not find Add to Cart button with any selector');
    }

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully added first product to cart",
      extractedData: {
        product: productInfo,
        cartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Get first product card
    const firstProduct = await page.$('.product-card:first-child');
    if (!firstProduct) {
      throw new Error('No product cards found on the page');
    }

    // Extract product information before adding to cart
    const productTitle = await firstProduct.$eval('.product-title', el => el.textContent.trim());
    const productPrice = await firstProduct.$eval('.product-price', el => el.textContent.trim());

    // Wait for and click the Add to Cart button
    const addToCartBtn = await firstProduct.$('.add-to-cart');
    if (!addToCartBtn) {
      throw new Error('Add to Cart button not found in first product card');
    }

    await addToCartBtn.click();

    // Wait for cart count to update
    await page.waitForFunction(
      () => {
        const cartCount = document.getElementById('cartCount');
        return cartCount && cartCount.textContent !== '0';
      },
      { timeout: 3000 }
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle,
        productPrice,
        cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
