import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first product card to load
    await page.waitForSelector('.product-card:first-child', { timeout: 10000 });

    // Extract product information before adding to cart
    const productInfo = await page.$eval('.product-card:first-child', (card) => {
      const title = card.querySelector('.product-title')?.textContent || 'Unknown Product';
      const price = card.querySelector('.product-price')?.textContent || 'Unknown Price';
      return { title, price };
    });

    // Click the Add to Cart button on the first product
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for cart count to update
    await page.waitForFunction(
      () => {
        const cartCount = document.getElementById('cartCount');
        return cartCount && cartCount.textContent !== '0';
      },
      { timeout: 5000 }
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Verify the cart count has increased
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle: productInfo.title,
        productPrice: productInfo.price,
        cartCount: cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

