import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Get initial cart count
    const initialCartCount = await page.$eval('#cartCount', el => el.textContent.trim());

    // Click the first product's Add to Cart button using multiple selector strategies
    const addToCartButton = await page.$('.product-card:first-child .add-to-cart') || 
                           await page.$('button:has-text("Add to Cart")') ||
                           await page.$('button[class*="add-to-cart"]');

    if (!addToCartButton) {
      throw new Error('Add to Cart button not found');
    }

    await addToCartButton.click();

    // Wait for cart count to update
    await page.waitForFunction(
      (initialCount) => {
        const currentCount = document.getElementById('cartCount').textContent.trim();
        return currentCount !== initialCount;
      },
      { timeout: 5000 },
      initialCartCount
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count
    const updatedCartCount = await page.$eval('#cartCount', el => el.textContent.trim());

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        initialCartCount,
        updatedCartCount,
        status: "Cart count increased from " + initialCartCount + " to " + updatedCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
