import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Get initial cart count
    const initialCartCount = await page.textContent('#cartCount');

    // Click the first product's Add to Cart button using multiple selector strategies
    await page.waitForSelector('.product-card:first-child .add-to-cart', { timeout: 3000 });
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for cart count to update
    await page.waitForFunction(
      (initialCount) => {
        const cartCount = document.getElementById('cartCount');
        return cartCount && parseInt(cartCount.textContent) > parseInt(initialCount);
      },
      initialCartCount,
      { timeout: 5000 }
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count and product details
    const updatedCartCount = await page.textContent('#cartCount');
    const productTitle = await page.textContent('.product-card:first-child .product-title');
    const productPrice = await page.textContent('.product-card:first-child .product-price');

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        cartCount: updatedCartCount,
        productTitle: productTitle?.trim(),
        productPrice: productPrice?.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Get initial cart count
    const initialCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);

    // Find first product card and click add to cart button
    const firstProductCard = await page.$('.product-card:first-child');
    await firstProductCard.waitForSelector('.add-to-cart');
    await firstProductCard.click('.add-to-cart');

    // Wait for cart count to update (with retry logic)
    let newCartCount = initialCartCount;
    for (let i = 0; i < 10; i++) {
      await page.waitForTimeout(500);
      newCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);
      if (newCartCount > initialCartCount) break;
    }

    // Verify cart count increased
    if (newCartCount <= initialCartCount) {
      throw new Error('Cart count did not increase after adding product');
    }

    // Take success screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    return {
      success: true,
      action: "Successfully added first product to cart",
      extractedData: {
        initialCartCount,
        finalCartCount: newCartCount,
        productAdded: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
      screenshots.push(path.join(screenshotsDir, 'step_error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Get first product card using multiple selector strategies
    const firstProduct = await page.$('.product-card:first-child') || 
                         await page.$('div[class*="product"]:first-child') ||
                         await page.$('div.product:first-child');

    if (!firstProduct) {
      throw new Error('First product card not found');
    }

    // Extract product title for verification
    const productTitle = await firstProduct.$eval('h3, .product-title, [class*="title"]', el => el.textContent.trim());

    // Find and click Add to Cart button using multiple strategies
    const addButton = await firstProduct.$('.add-to-cart') ||
                     await firstProduct.$('button:has-text("Add to Cart")') ||
                     await firstProduct.$('button:contains("Add")');

    if (!addButton) {
      throw new Error('Add to Cart button not found');
    }

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the button
    await addButton.click();

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_add.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_add.png'));

    // Get updated cart count
    const cartCount = await page.$eval('#cartCount', el => el.textContent);

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle,
        cartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
