import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Get first product information before adding to cart
    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent.trim());
    const initialCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);

    // Click add to cart button on first product
    await page.click('.product-card:first-child .add-to-cart');

    // Wait for cart count to update
    await page.waitForFunction(
      (initialCount) => {
        const cartCount = parseInt(document.querySelector('#cartCount').textContent) || 0;
        return cartCount > initialCount;
      },
      { timeout: 5000 },
      initialCartCount
    );

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_added_to_cart.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_added_to_cart.png'));

    // Get updated cart count
    const updatedCartCount = await page.$eval('#cartCount', el => parseInt(el.textContent) || 0);

    return {
      success: true,
      action: "Added first product to cart successfully",
      extractedData: {
        productTitle,
        initialCartCount,
        updatedCartCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to add first product to cart",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

