import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 10000 });

    // Find the first product card
    const firstProductCard = await page.$('.product-card:first-child');
    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Take screenshot of first product
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_first_product.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_first_product.png'));

    // Find and click the Add to Cart button using multiple selector strategies
    const addToCartButton = await firstProductCard.$('button:has-text("Add to Cart"), .add-to-cart, [class*="add-to-cart"]');
    if (!addToCartButton) {
      throw new Error('Add to Cart button not found on first product');
    }

    // Click the button
    await addToCartButton.click();

    // Wait for cart count to update
    await page.waitForFunction(() => {
      const cartCount = document.getElementById('cartCount');
      return cartCount && cartCount.textContent !== '0';
    }, { timeout: 5000 });

    // Take screenshot after adding to cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_add.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_add.png'));

    // Extract cart count to verify success
    const cartCount = await page.$eval('#cartCount', el => el.textContent);
    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent);

    return {
      success: true,
      action: "Successfully added first product to cart",
      extractedData: {
        cartCount: cartCount,
        productAdded: productTitle.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to add product to cart",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
