import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the shopping cart icon to be visible and click it
    await page.waitForSelector('.cart', { timeout: 5000 });
    const cartIcon = await page.$('.cart');
    if (cartIcon) {
      await cartIcon.click();
      await page.waitForSelector('#cartModal', { timeout: 5000 });

      // Take a screenshot after opening the cart modal
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));
    }

    return {
      success: true,
      action: "Opened the shopping cart modal",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
