import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the shopping cart element to be visible
    await page.waitForSelector('.cart', { state: 'visible' });

    // Click on the shopping cart icon
    await page.click('.cart');

    // Wait for the cart modal to appear
    await page.waitForSelector('#cartModal', { state: 'visible' });

    // Take screenshot after opening cart modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_modal_open.png'));

    // Extract cart information to verify success
    const cartCount = await page.textContent('#cartCount');
    const isModalVisible = await page.isVisible('#cartModal');

    return {
      success: true,
      action: "Successfully opened shopping cart modal by clicking on cart icon",
      extractedData: {
        cartItemCount: cartCount,
        modalVisible: isModalVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open shopping cart modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

