import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the cart element to be visible using multiple selector strategies
    const cartSelector = '.cart';
    await page.waitForSelector(cartSelector, { state: 'visible' });

    // Click on the shopping cart icon
    await page.click(cartSelector);

    // Wait for cart modal to appear using ID selector from category hint
    await page.waitForSelector('#cartModal', { state: 'visible' });

    // Take screenshot after opening cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Extract cart data to verify modal opened successfully
    const cartItems = await page.$$eval('#cartModal .cart-item', items => 
      items.map(item => ({
        title: item.querySelector('.item-title')?.textContent || 'N/A',
        price: item.querySelector('.item-price')?.textContent || 'N/A'
      }))
    );

    const cartTotal = await page.$eval('#cartModal .cart-total', el => el.textContent);

    return {
      success: true,
      action: "Successfully opened shopping cart modal by clicking cart icon",
      extractedData: {
        cartItemsCount: cartItems.length,
        cartItems: cartItems,
        cartTotal: cartTotal.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open shopping cart modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
