import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the shopping cart icon
    await page.waitForSelector('.cart');
    await page.click('.cart');

    // Wait for cart modal to appear
    await page.waitForSelector('#cartModal', { state: 'visible' });

    // Take screenshot after opening cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_open.png'));

    // Extract cart information
    const cartData = await page.evaluate(() => {
      const cartItems = document.querySelectorAll('.cart-item');
      return {
        itemCount: cartItems.length,
        isVisible: document.getElementById('cartModal').style.display !== 'none',
        cartTitle: document.querySelector('.cart-modal-title')?.textContent || ''
      };
    });

    return {
      success: true,
      action: "Successfully opened shopping cart modal",
      extractedData: cartData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open shopping cart modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

