import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the cart element using multiple selector strategies
    await page.waitForSelector('.cart, [class*="cart"], [onclick*="cart"], [onclick*="Cart"]', { timeout: 5000 });

    // Click the cart icon using a more specific selector
    await page.click('.cart');

    // Wait for the cart modal to appear using ID selector from category hint
    await page.waitForSelector('#cartModal', { timeout: 3000 });

    // Take screenshot after opening cart
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_cart_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_cart_opened.png'));

    // Extract cart data to verify success
    const cartData = await page.evaluate(() => {
      const modal = document.querySelector('#cartModal');
      return {
        modalVisible: modal ? modal.style.display !== 'none' : false,
        cartCount: document.querySelector('.cart-count')?.innerText || '0'
      };
    });

    return {
      success: true,
      action: "Successfully clicked shopping cart icon and opened cart modal",
      extractedData: cartData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open shopping cart modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
