import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Perform a search
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Click the list view button to switch to list view
    await page.waitForSelector(".view-btn[title='List View']");
    await page.click(".view-btn[title='List View']");
    await page.waitForLoadState('networkidle');

    // Take screenshot after switching to list view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_list_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_list_view.png'));

    // Verify if the list view is displayed
    const isListView = await page.$('.products-list') !== null;

    return {
      success: true,
      action: "Switched from grid view to list view",
      extractedData: { isListView },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

