      success: true,
      action: "Switched to List View after performing a search",
      extractedData: { isListViewActive },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

