import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Perform search
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    // Wait for search results and take screenshot
    await page.waitForSelector('.product-item, .product-grid, .products-container', { timeout: 10000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Switch to list view using multiple selector strategies
    const listViewSelectors = [
      '.view-btn[title="List View"]',
      '.products-list',
      'button:has-text("List View")',
      'a:has-text("List View")'
    ];

    let listViewFound = false;
    for (const selector of listViewSelectors) {
      try {
        await page.waitForSelector(selector, { timeout: 2000 });
        await page.click(selector);
        listViewFound = true;
        break;
      } catch (e) {
        continue;
      }
    }

    if (!listViewFound) {
      throw new Error('List view button not found');
    }

    // Wait for view change and verify
    await page.waitForSelector('.list-view, [data-view="list"]', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_list_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_list_view.png'));

    // Extract data to verify task completion
    const extractedData = {
      searchPerformed: true,
      listViewActive: await page.$eval('.list-view, [data-view="list"]', el => true).catch(() => false),
      productCount: await page.$$eval('.product-item, .product', items => items.length)
    };

    return {
      success: true,
      action: "Performed search for 'electronics' and switched to list view mode",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Perform search for electronics
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    // Wait for search results to load
    await page.waitForSelector('.product-item, .product-grid, .products-container', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Try multiple selector strategies for list view button
    const listViewSelectors = [
      '.view-btn[title="List View"]',
      '.products-list',
      'button:has-text("List View")',
      'button[title*="List"]',
      'button[class*="list"]',
      'button[class*="view"]'
    ];

    let listViewButton = null;
    for (const selector of listViewSelectors) {
      const element = await page.$(selector);
      if (element) {
        listViewButton = element;
        break;
      }
    }

    if (!listViewButton) {
      throw new Error('List view button not found with any selector');
    }

    // Click the list view button
    await listViewButton.click();

    // Wait for view mode change
    await page.waitForTimeout(1000);
    await page.waitForSelector('.product-list, .list-view, [class*="list"]', { state: 'visible' });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_list_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_list_view.png'));

    // Extract data to verify task completion
    const viewMode = await page.$eval('body', el => {
      return el.classList.contains('list-view') || 
             Array.from(el.querySelectorAll('*'))
               .some(el => el.className.includes('list-view') || el.className.includes('products-list'));
    });

    return {
      success: true,
      action: "Performed search for 'electronics' and switched to list view mode",
      extractedData: {
        viewMode: viewMode ? 'list' : 'unknown',
        searchTerm: 'electronics'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to switch to list view mode",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Perform search
    await page.fill('#searchInput', 'electronics');
    await page.click('button.search-btn');

    // Wait for search results to load
    await page.waitForSelector('.product-card, .products-container', { timeout: 10000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Switch to list view using multiple selector strategies
    const listViewSelectors = [
        '.view-btn[title="List View"]',
        '.products-list',
        'button:has-text("List View")',
        'button[title*="List"]'
    ];

    let listViewButton = null;
    for (const selector of listViewSelectors) {
        listViewButton = await page.$(selector);
        if (listViewButton) break;
    }

    if (!listViewButton) {
        throw new Error('List view button not found with any selector');
    }

    await listViewButton.click();

    // Wait for view mode change and verify
    await page.waitForTimeout(1000); // Allow time for UI update
    await page.waitForSelector('.list-view-active, [data-view-mode="list"]', { timeout: 5000 });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_list_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_list_view.png'));

    // Extract verification data
    const extractedData = await page.evaluate(() => {
        const viewMode = window.currentViewMode || 'unknown';
        const productElements = document.querySelectorAll('.product-card, .product-item');
        return {
            viewMode,
            productCount: productElements.length,
            hasListLayout: Array.from(productElements).some(el => 
                el.classList.contains('list-view') || 
                el.closest('.products-list')
            )
        };
    });

    return {
      success: true,
      action: "Performed search for 'electronics' and switched to list view mode",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
