import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first product card to open its detailed product information modal
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');
    await page.waitForSelector('#productModal');

    // Take screenshot of the product detail modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Extract meaningful data from the product modal
    const productTitle = await page.textContent('#productModal .product-title');
    const productPrice = await page.textContent('#productModal .product-price');

    return {
      success: true,
      action: "Opened product detail modal and extracted product information",
      extractedData: {
        title: productTitle,
        price: productPrice
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

