import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the first product card to open the product detail modal
    await page.waitForSelector('.product-card:first-child');
    await page.click('.product-card:first-child');

    // Wait for the product modal to be visible
    await page.waitForSelector('#productModal', { visible: true });

    // Take a screenshot of the product modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_product_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_product_modal.png'));

    // Extract information from the product modal
    const productTitle = await page.$eval('#productModal .product-title', el => el.innerText);
    const productPrice = await page.$eval('#productModal .product-price', el => el.innerText);

    return {
      success: true,
      action: "Opened the product detail modal successfully",
      extractedData: {
        title: productTitle,
        price: productPrice
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
