import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load using multiple selector strategies
    await page.waitForSelector('.product-card', { timeout: 5000 });
    await page.waitForSelector('img[src*="amazon.com/images/I/"]', { timeout: 5000 });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click on the first product card using a more specific selector
    await page.click('.product-card:first-child');

    // Wait for the modal to appear with multiple verification methods
    await page.waitForSelector('#productModal', { timeout: 3000 });
    await page.waitForSelector('.modal-content', { timeout: 3000 });

    // Verify modal is visible
    const modalVisible = await page.isVisible('#productModal');
    if (!modalVisible) {
      throw new Error('Product modal did not appear after clicking product card');
    }

    // Take screenshot after modal appears
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_open.png'));

    // Extract product details from the modal
    const productData = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      return {
        title: modal.querySelector('.product-title')?.textContent?.trim() || 'Not found',
        price: modal.querySelector('.product-price')?.textContent?.trim() || 'Not found',
        rating: modal.querySelector('.product-rating')?.textContent?.trim() || 'Not found',
        description: modal.querySelector('.product-description')?.textContent?.trim() || 'Not found'
      };
    });

    return {
      success: true,
      action: "Successfully opened product detail modal by clicking on the first product card",
      extractedData: productData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error_state.png') });
      screenshots.push(path.join(screenshotsDir, 'error_state.png'));
    } catch (e) {
      console.log('Could not take error screenshot:', e.message);
    }

    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

