        productTitle: modal.querySelector('.product-title')?.textContent?.trim() || null,
        productPrice: modal.querySelector('.product-price')?.textContent?.trim() || null,
        productRating: modal.querySelector('.product-rating')?.textContent?.trim() || null,
        isModalVisible: modal && getComputedStyle(modal).display !== 'none'
      };
    });

    return {
      success: true,
      action: "Successfully clicked on first product card and opened product detail modal",
      extractedData,
      screenshots,
      error: null
