import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the first product card
    await page.waitForSelector('.product-card:first-child', { timeout: 5000 });
    await page.click('.product-card:first-child');

    // Wait for the product modal to appear
    await page.waitForSelector('#productModal', { timeout: 5000 });

    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract product information from the modal
    const productInfo = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      if (!modal) return null;

      return {
        title: modal.querySelector('.product-title')?.textContent?.trim(),
        price: modal.querySelector('.product-price')?.textContent?.trim(),
        rating: modal.querySelector('.product-rating')?.textContent?.trim(),
        description: modal.querySelector('.product-description')?.textContent?.trim()
      };
    });

    return {
      success: true,
      action: "Opened product detail modal for the first product",
      extractedData: productInfo || {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Take screenshot of products before interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_products_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_products_visible.png'));

    // Click on the first product card using multiple selector strategies
    const firstProductCard = await page.$('.product-card:first-child');
    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Extract product title before clicking to verify later
    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent.trim());

    // Click the first product card
    await firstProductCard.click();

    // Wait for the modal to appear with multiple verification strategies
    await Promise.all([
      page.waitForSelector('#productModal', { state: 'visible', timeout: 3000 }),
      page.waitForSelector('.modal-content', { state: 'visible', timeout: 3000 })
    ]);

    // Take screenshot of modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_opened.png'));

    // Verify modal content matches the clicked product
    const modalTitle = await page.$eval('#productModal .product-title', el => el.textContent.trim());

    if (modalTitle !== productTitle) {
      throw new Error('Modal title does not match clicked product');
    }

    // Extract additional product details from modal
    const modalPrice = await page.$eval('#productModal .product-price', el => el.textContent.trim());
    const modalRating = await page.$eval('#productModal .product-rating', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully opened product detail modal by clicking first product card",
      extractedData: {
        productTitle: modalTitle,
        productPrice: modalPrice,
        productRating: modalRating
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

