    await page.click('.product-card:first-child');

    // Wait for the product modal to appear
    await page.waitForSelector('#productModal', { 
      state: 'visible', 
      timeout: 3000 
    });

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract product information from modal
    const extractedData = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      return {
        productTitle: modal.querySelector('.product-title')?.textContent?.trim() || '',
        productPrice: modal.querySelector('.product-price')?.textContent?.trim() || '',
        productDescription: modal.querySelector('.product-description')?.textContent?.trim() || '',
        modalVisible: modal.checkVisibility()
      };
    });

    return {
      success: true,
      action: "Successfully clicked first product card and opened product detail modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
