    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent.trim());

    // Click the first product card
    await firstProductCard.click();

    // Wait for product modal to appear
    await page.waitForSelector('#productModal', { 
      state: 'visible',
      timeout: 5000 
    });

    // Verify modal content by checking if product title matches
    const modalTitle = await page.$eval('#productModal .product-title', el => el.textContent.trim());
    if (productTitle !== modalTitle) {
      throw new Error('Product modal title does not match clicked product');
    }

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract additional product details from modal
    const productDetails = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      return {
        title: modal.querySelector('.product-title')?.textContent.trim(),
        price: modal.querySelector('.product-price')?.textContent.trim(),
        rating: modal.querySelector('.product-rating')?.textContent.trim(),
        description: modal.querySelector('.product-description')?.textContent.trim()
      };
    });

    return {
      success: true,
      action: "Successfully opened product detail modal by clicking on first product card",
      extractedData: productDetails,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

