    const productTitle = await page.$eval('.product-card:first-child .product-title', el => el.textContent.trim());

    // Click the product card
    await firstProductCard.click();

    // Wait for the modal to appear with multiple verification strategies
    await page.waitForSelector('#productModal', { timeout: 5000 });
    await page.waitForSelector('.modal-content', { timeout: 5000 });

    // Verify modal contains the expected product title
    const modalTitle = await page.$eval('#productModal .product-title', el => el.textContent.trim());

    if (productTitle !== modalTitle) {
      throw new Error('Modal title does not match clicked product');
    }

    // Take screenshot of opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract additional product details from modal
    const extractedData = {
      productTitle: modalTitle,
      price: await page.$eval('#productModal .product-price', el => el.textContent.trim()),
      rating: await page.$eval('#productModal .product-rating', el => el.textContent.trim()),
      description: await page.$eval('#productModal .product-description', el => el.textContent.trim())
    };

    return {
      success: true,
      action: "Successfully opened product detail modal for: " + modalTitle,
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
