    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract product details from the modal
    const extractedData = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      return {
        title: modal.querySelector('.product-title')?.textContent?.trim() || '',
        price: modal.querySelector('.product-price')?.textContent?.trim() || '',
        rating: modal.querySelector('.product-rating')?.textContent?.trim() || '',
        description: modal.querySelector('.product-description')?.textContent?.trim() || ''
      };
    });

    return {
      success: true,
      action: "Successfully opened product detail modal by clicking the first product card",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on the first product card using multiple selector strategies
    await page.waitForSelector('.product-card', { state: 'visible' });

    // Try multiple selector strategies for robustness
    const firstProductCard = await page.$('.product-card:first-child') || 
                            await page.$('div.product-card') ||
                            await page.$('[class*="product-card"]');

    if (!firstProductCard) {
      throw new Error('No product card found on the page');
    }

    await firstProductCard.click();
    await page.waitForTimeout(1000); // Brief pause for modal animation

    // Wait for the product modal to appear using multiple selector strategies
    await page.waitForSelector('#productModal', { state: 'visible' });

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract product details from the modal
    const productDetails = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      if (!modal) return null;

      return {
        title: modal.querySelector('.product-title')?.textContent?.trim() || 
              modal.querySelector('h1, h2, h3')?.textContent?.trim(),
        price: modal.querySelector('.product-price')?.textContent?.trim() ||
              modal.querySelector('[class*="price"]')?.textContent?.trim(),
        isVisible: modal.offsetParent !== null
      };
    });

    return {
      success: true,
      action: "Successfully clicked on the first product card and opened product detail modal",
      extractedData: productDetails || { message: "Modal opened but product details not found" },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Click the first product card using multiple selector strategies
    await page.click('.product-card:first-child');

    // Wait for the product modal to appear
    await page.waitForSelector('#productModal', { 
      state: 'visible',
      timeout: 3000 
    });

    // Take screenshot of the opened modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract product information from the modal
    const productData = await page.evaluate(() => {
      const modal = document.querySelector('#productModal');
      return {
        title: modal.querySelector('.product-title')?.textContent?.trim() || 'Not found',
        price: modal.querySelector('.product-price')?.textContent?.trim() || 'Not found',
        rating: modal.querySelector('.product-rating')?.textContent?.trim() || 'Not found'
      };
    });

    return {
      success: true,
      action: "Successfully opened product detail modal for the first product",
      extractedData: productData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
