import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Click the first product card using multiple selector strategies
    const firstProductCard = await page.$('.product-card:first-child');
    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Get product details before clicking
    const productTitle = await firstProductCard.$eval('.product-title', el => el.textContent.trim());

    // Click the product card
    await firstProductCard.click();

    // Wait for modal to appear with multiple verification strategies
    await Promise.all([
      page.waitForSelector('#productModal', { state: 'visible', timeout: 3000 }),
      page.waitForSelector('.modal-content', { state: 'visible', timeout: 3000 })
    ]);

    // Take screenshot of modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract product details from modal
    const modalTitle = await page.$eval('#productModal .product-title', el => el.textContent.trim());
    const modalPrice = await page.$eval('#productModal .price', el => el.textContent.trim());
    const modalDescription = await page.$eval('#productModal .product-description', el => el.textContent.trim());

    // Verify modal contains expected product information
    if (!modalTitle.includes(productTitle)) {
      throw new Error('Modal title does not match clicked product');
    }

    return {
      success: true,
      action: "Successfully opened product detail modal by clicking first product card",
      extractedData: {
        productTitle: modalTitle,
        price: modalPrice,
        description: modalDescription.substring(0, 100) + '...'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for product cards to load
    await page.waitForSelector('.product-card', { timeout: 5000 });

    // Click on the first product card using multiple selector strategies
    const firstProductCard = await page.$('.product-card:first-child');
    if (!firstProductCard) {
      throw new Error('No product cards found on the page');
    }

    // Extract product title before clicking for verification
    const productTitle = await page.$eval('.product-card:first-child .product-title', 
      el => el.textContent.trim());

    // Click the first product card
    await firstProductCard.click();

    // Wait for the product modal to appear with multiple verification strategies
    await Promise.all([
      page.waitForSelector('#productModal', { state: 'visible', timeout: 3000 }),
      page.waitForSelector('.modal-content', { state: 'visible', timeout: 3000 }),
      page.waitForSelector('.product-details', { state: 'visible', timeout: 3000 })
    ]);

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract product details from modal for verification
    const modalProductTitle = await page.$eval('#productModal .product-title', 
      el => el.textContent.trim());

    const modalProductPrice = await page.$eval('#productModal .product-price', 
      el => el.textContent.trim());

    // Verify the modal contains the expected product
    if (productTitle !== modalProductTitle) {
      throw new Error('Modal product title does not match clicked product');
    }

    return {
      success: true,
      action: "Successfully clicked first product card and opened product detail modal",
      extractedData: {
        productTitle: modalProductTitle,
        productPrice: modalProductPrice
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if available
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error_state.png') });
      screenshots.push(path.join(screenshotsDir, 'error_state.png'));
    } catch (screenshotError) {
      console.error('Failed to take error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to open product detail modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
