      return cards.map(card => parseFloat(card.querySelector('.price').innerText.replace('$', '')));
    });

    const isSorted = sortedProducts.every((value, index, array) => !index || (value >= array[index - 1]));

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { isSorted },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

