import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be visible and enter a search term
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Click the search button
    await page.click('button.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_results.png'));

    // Wait for the sort dropdown to be visible and select 'Price: Low to High'
    await page.waitForSelector('select.search-category');
    await page.selectOption('select.search-category', 'price_low_to_high');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_sort_price_low_to_high.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_sort_price_low_to_high.png'));

    // Verify if the products are sorted by price
    const products = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => {
        const title = card.querySelector('.product-title').innerText;
        const price = parseFloat(card.querySelector('.product-price').innerText.replace('$', ''));
        return { title, price };
      });
    });

    // Check if products are sorted correctly
    const isSorted = products.every((product, index, arr) => index === 0 || product.price >= arr[index - 1].price);

    return {
      success: true,
      action: "Sorted products by price from low to high",
      extractedData: { isSorted, products },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

