import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Interact with the search input to perform a product search
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', 'electronics');
    await page.waitForTimeout(500); // Wait for a moment to simulate user typing

    // Click the search button
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');

    // Take screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    // Wait for product cards to be visible
    await page.waitForSelector('.products-grid .product-card');

    // Use the sort dropdown to sort by 'Price: Low to High'
    await page.waitForSelector('select.search-category');
    await page.selectOption('select.search-category', 'all'); // Select 'All' category (if needed)

    // Wait for the sort dropdown to be visible and select 'Price: Low to High'
    await page.waitForSelector('select[name="undefined"]');
    await page.selectOption('select[name="undefined"]', 'price-ascending'); // Assuming this is the value for 'Low to High'

    // Wait for products to be sorted
    await page.waitForTimeout(1000); // Wait for sorting to complete

    // Take final screenshot after sorting
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_sorted_products.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_sorted_products.png'));

    // Extracting data to verify sorting completion
    const sortedProducts = await page.$$eval('.products-grid .product-card', cards => {
      return cards.map(card => ({
        title: card.querySelector('.product-title').innerText,
        price: card.querySelector('.product-price').innerText,
      }));
    });

    return {
      success: true,
      action: "Successfully sorted products by price from low to high",
      extractedData: sortedProducts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
